function xml_parse_util(xmlfile)%% Function to parse xml files.%% Anthony L. Daniell - 31 March 2017%[fid, msg] = fopen (xmlfile, "rt")# Do the actual I/O hereâ€¦depth=1xml_header_block = []do     data = fgetl(fid) ;    %% process string%        iString = 0 ;    do        iString++ ;        curtest = data(iString)        if curtest == " "      % stay in start state            iString++ ;            continue        elseif curtest == "<"  % move to prep state            iString++ ;            curtest = data(iString)            if curtest=="?"    % xml header block                do                    iString++ ;                    curtest=data(iString);                    xml_header_block = [xml_header_block curtest]                until curtest==">"  % exit xml header block            endif        else            error("xml_parse_util:  XML file is incorrectly formatted.  Halting.")        endif%        until iString==length(data)    until data==-1  % EOF reached. fclose (fid);%% End of script%